public class Example2_19_10 {

	/**
	 * @param args
	 */
// ***
//  **
//   *
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=5,j,k;
		while(i>=1)
		{
			j=5-i;
			while(j>=1)
			{
				System.out.print(" ");
				j--;
			}
			k=1;
			while(k<=i)
			{
				System.out.print("*");
				k++;
			}
			System.out.println("");
			i--;
		}

	}

}
