public class Example2_9_00 {

	/**
	 * @param args
	 */
//   *
//  **
// ***
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1,j,k;
		while(i<=5)
		{
			k=5-i;
			while(k>=1)
			{
				System.out.print(" ");
				k--;
			}
			j=1;
			while(j<=i)
			{
				System.out.print("*");
				j++;
			}
			System.out.println("");
			i++;
		}

	}

}
